function colebrook(rel_roughness,reynold,x0=.15,tol=1e-10){left=1/(Math.sqrt(x0));right=-2*Math.log10((rel_roughness / 3.71)+(2.51 /(reynold*Math.sqrt(x0))));let fator=left/right;if(Math.abs(1-fator)<=tol){return x0}var fdof=1;if(left-right>1){fdof=left-right-1;fdof /=2;fdof+=1}else if(right-left>1){fdof=right-left-1;fdof /=2;fdof+=1;fdof=1/fdof}return colebrook(rel_roughness,reynold,x0*fator*fdof,tol)}